import {books} from "./BookData"
import { useState } from "react";

const Main = ()=>{
    const [search,setSearch] = useState('The Jungle Book');

    return(
        <div>


          <article className="book-list">
            {books.map((book)=>{
                return(
                    <div className="book" key = {book.id}  >
                        <img src = {book.img} alt = {book.title}/>
                        <h1>{book.title}</h1>
                    </div>
                )
            })}
        </article>
        </div>
    )
}

export default Main;
